import React from 'react'
import Img1 from "../../assets/images/phone-g-call.svg";
import Img2 from "../../assets/images/certificate-r.svg";
import Img3 from "../../assets/images/scissors.svg";
import Img4 from "../../assets/images/devices.svg";
import Img5 from "../../assets/images/certificate.svg";
import styled from 'styled-components';
export default function SchoolTefun() {
  return (
        <SectionContainer>
            <WrapperContainer>
                <Heading>
                    <Title>
                        സ്കൂളുകൾക്ക്  <Sm>Tefun<br/></Sm> പ്രോഗ്രാമിന്റെ ഭാഗമാകാം
                    </Title>
                </Heading>
                <Details>
                    <Card>

                        <DetailContainer>
                            <ImageContainer>
                                <Img src={Img1} alt="Image"/>
                            </ImageContainer>
                        </DetailContainer>
                        <Discription><Bold>Tefun </Bold>പ്രോഗ്രാമിന്റെ ഭാഗമാകാൻ ആഗ്രഹിക്കുന്ന സ്കൂളുകൾക്ക് <Bold>Talrop</Bold>-ന്റെ ടീമുമായി <Number>858 999 8744</Number> എന്ന നമ്പറിൽ കോൺടാക്ട് ചെയ്യാവുന്നതാണ്.</Discription>
                    </Card>
                    <Card>
                        <Count>2</Count>
                        <DetailContainer>
                            <ImageContainer>
                                <Img src={Img2} alt="Image"/>
                            </ImageContainer>
                        </DetailContainer>
                        <Discription>സ്കൂളിന്റെ ലോഗോയും അവശ്യ വിവരങ്ങളും നൽകുക.</Discription>
                    </Card>
                    <Card>
                        <Count>3</Count>
                        <DetailContainer>
                            <ImageContainer>
                                <Img src={Img3} alt="Image"/>
                            </ImageContainer>
                        </DetailContainer>
                        <Discription>സ്കൂൾ നിശ്ചയിക്കുന്ന ഉദ്ഘാടന ദിനത്തിൽ ആവശ്യമായ Inauguration Video  ഡിജിറ്റലായി<Bold>Talrop</Bold>നൽകുന്നതാണ്.</Discription>
                    </Card>
                    <Card>
                        <Count>4</Count>
                        <DetailContainer>
                            <ImageContainer>
                                <Img src={Img4} alt="Image"/>
                            </ImageContainer>
                        </DetailContainer>
                        <Discription>Mobile, Tab, Laptop, Desktop Computer എന്നിവയിൽ ഏതെങ്കിലും ഡിവൈസുകൾ ഉപയോഗിച്ച്  Talrop ലഭ്യമാക്കുന്ന Web,Android, iOS  പ്ലാറ്റ്ഫോമുകളിലൂടെ കുട്ടികൾക്ക് പഠിക്കുവാൻ സാധിക്കും.</Discription>
                    </Card>
                    <Card>
                        <Count>5</Count>
                        <DetailContainer>
                            <ImageContainer>
                                <Img src={Img5} alt="Image"/>
                            </ImageContainer>
                        </DetailContainer>
                        <Discription>വിജയകരമായി പൂർത്തീകരിക്കുന്ന വിദ്യാർത്ഥികൾക്ക് ടാൽറോപ് സർട്ടിഫിക്കറ്റ് നൽകുന്നു.</Discription>
                    </Card>
                </Details>
                <JoinButton>Join for free</JoinButton>
            </WrapperContainer>
        </SectionContainer>
  )
}

const SectionContainer = styled.section`
    padding: 50px 0px;
`;
const WrapperContainer = styled.section`
    margin: 0 auto;
    max-width: 1280px;
    width: 90%;
`;
const Heading = styled.h4`
    text-align: center;
    margin-bottom: 80px;
`;
const Title = styled.span`
    color: #0fa76f;
    font-size: 40px;
    display: inline;
    font-family: "EGGIndulekhaUni";
`;
const Sm = styled.span`
    font-size: 40px;
    font-family: "gordita_medium";
    color: #000;
`;
