    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-wrap: wrap;
    margin: 0px auto 35px;
    position: relative;
    gap: 30px;
`;
const Card = styled.li`
    width: 30%;
    position: relative;
    margin-bottom: 30px;
    text-align: center;
`;
