import React, { useState } from 'react'
import styled from 'styled-components'
import school1 from "../../assets/images/sch1.jpg"
import Location from "../../assets/images/Location.svg"

export default function Campuses() {
    const [data]= useState([
        {
            id:1,
            image: school1,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },
        {
            id:2,
            image: school2,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },
        {
            id:3,
            image: school3,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },
        {
            id:4,
            image: school4,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },
        {
            id:5,
            image: school5,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },
        {
            id:6,
            image: school6,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },
        {
            id:7,
            image: school7,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },
        {
            id:1,
            image: school8,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },
        {
            id:1,
            image: school9,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },
        {
            id:1,
            image: school10,
            name: "Govt LP School Kadinamkulam",
            place: "Thiruvananthapuram",
        },

    ])
  return (
        <CampusSection>
            <WrapperContainer>
                <AssosTitle><Sm>Associated</Sm> Campuses</AssosTitle>
                <CampusD>Various institutions have joined hands with us, to train their students and improve their employability skills, along with moulding them to be future entrepreneurs.</CampusD>
                <SchoolD>
                    <College>
                        <InstituteImage>
                            <Img src={school1} alt="Image"/>
                        </InstituteImage>
                        <Name>{}</Name>
                        <Place><PlaceImg src={Location} alt="Image" />{}</Place>
                    </College>
                </SchoolD>
                <More>
					And <Bold>300</Bold> More Campus All Over Kerala
				</More>
            </WrapperContainer>
        </CampusSection>
  )
}

const CampusSection = styled.section`
    background-color: #f9fcf5;
	padding: 5% 0;
`;
const WrapperContainer = styled.section`
    width:90%;
	margin: 0 auto;
`;
const AssosTitle = styled.h3`
    text-align: center;
    font-size: 40px;
    font-weight: 800;
    margin-bottom: 1%;
`;
const Sm = styled.span`
    color:#59a670;
    font-size: 40px;
    font-weight: 800;
    margin-bottom: 1%;
`;
const CampusD = styled.p`
    color: #6e6e6e;
    text-align: center;
    width: 60%;
    margin: 0 auto;
    font-size: 18px;
    margin-bottom: 3%;`;
const SchoolD = styled.ul`
    width: 100%;
`;
const College = styled.li`
    width: 45% !important ;
    margin-bottom: 15% !important;
    margin-right: 3% !important;
	&:nth-last-child(-n+6){
		margin-bottom: 0%;
	}
`;
const InstituteImage = styled.div`
    width: 50%;
    margin: 0 auto 3%;
`;
const Img = styled.img`
    width: 100%;
    display: block;
`;
const Name = styled.h4`
    width: 100%;
    text-align: center;
    font-size: 18px;
    margin-bottom: 3%;
`;
const Place = styled.h5`
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 11px;
    color: #59a670;
    font-size: 17px;
    font-weight: 700;
`;
const PlaceImg = styled.img`
    transform: translateY(-2px);
    margin-right: 6px;
    width: 12px;
    filter: invert(100%) sepia(93%) saturate(1416%) hue-rotate(49deg) brightness(119%) contrast(52%);
`;
const More = styled.p`
    text-align: center;
    font-size: 23px;
    font-weight: 700;
	margin-top: 6%;
`;
const Bold = styled.b`
    color: #59a670;
	font-weight: 700;
`;
