import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import bannerImage from "../../assets/images/bran";
import tefun from "../../assets/images/Tefun-logo.svg";
import spot from "../../assets/images/spot-min.jpeg";
import arrow from "../../assets/images/arrow.svg";
import apple_grey from "../../assets/images/grey-app-play.svg";
import android_grey from "../../assets/images/grey-and-play.svg";
import elipse from "../../assets/images/elipse.svg";
import line from "../../assets/images/lines-bg.svg";

export default function Spotlight() {
    return (
        <>
            <SpotSection>
                <SpotBg>
                    <SpotDetails className="details">
                        <Tefun>
                            <img src={tefun} alt="tefun" />
                        </Tefun>
                        <h4>Technology Fundamental Program</h4>
                        <h3>
                            കേരളത്തിലെ <span>20 ലക്ഷം </span>സ്കൂൾ വിദ്യാർത്ഥികൾക്ക് സൗജന്യമായി ടെക്ക്നോളജി പഠിക്കാം!
                        </h3>
                        <p>
                            ടാൽറോപ്പിന്റെ OTT കമ്മ്യൂണിറ്റി പ്ലാറ്റ്ഫോമായ 'യായ്'-ലൂടെ തികച്ചും സൗജന്യമായി ഇപ്പോൾ തന്നെ പഠനം ആരംഭിക്കൂ. <br /> Tefun-ലൂടെ ടെക്ക്നോളജിയുടെ വിസ്മയലോകം ഇനിയിതാ
                            നിങ്ങളിലേക്ക്. ടെക്ക്-പഠനം ഇനി കൂടുതൽ എളുപ്പമാക്കാൻ 'Yiaai' മൊബൈൽ ആപ്പ് ഡൗൺലോഡ് ചെയ്യൂ.
                        </p>
                        <Powered>
                            <img src={bannerImage} alt="media" />
                        </Powered>
                        <Subscribe>
                            <SubscribeBtn to="">Join for free</SubscribeBtn>
                            <RegGred>
                                <CampusReg to="">Register school</CampusReg>
                            </RegGred>
                        </Subscribe>
                    </SpotDetails>
                    <SpotImgContainer>
                        <SpotImg>
                            <Hero>
                                <img className="hero" src={spot} alt="Icon" loading="lazy" />
                            </Hero>
                            <ArrowIcon src={arrow} alt="arrow" />
                        </SpotImg>
                        <StoreContent>
                            <Store>
                                <App target="_blank" href="https://apps.apple.com/in/app/yiaai-ott-community-platform/id1609634713">
                                    <img src={apple_grey} alt="apple" />
                                </App>
                                <App target="_blank" href="https://play.google.com/store/apps/details?id=com.yiaai">
                                    <img src={android_grey} alt="Android" />
                                </App>
                            </Store>
                        </StoreContent>
                    </SpotImgContainer>
                </SpotBg>
            </SpotSection>
        </>
    );
}

const SpotSection = styled.section`
    max-width: 1650px;
    margin: 0 auto;
    margin-top: 130px;
    padding-bottom: 80px;
    position: relative;
    &::after {
        content: "";
        position: absolute;
        width: 200px;
        height: 200px;
        right: 2px;
        bottom: 5%;
        z-index: -1;
        background: url(${elipse}) no-repeat;
    }
`;
const SpotBg = styled.div`
    width: 95%;
    margin: 0 auto;
    border-radius: 35px;
    overflow: hidden;
    background-color: #f3f9eb;
    background-image: url(${line});
    background-position: top 0 left 10%;
    background-repeat: no-repeat;
    background-size: 1000px;
    padding: 5% 50px;
    display: flex;
    justify-content: space-between;
`;

const Powered = styled.div`
    margin-bottom: 25px;
    width: 80%;
    align-items: center;
    & img {
        display: block;
        width: 100%;
    }
`;
const Steyp = styled.span`
    padding: 5px 10px;
    height: 80px;
    background-color: #fff;
    border: 1px solid #8eceab;
    margin-right: 5px;
    border-radius: 10px;
    & small {
        display: block;
        font-size: 12px;
        font-family: "gordita_medium";
        position: relative;
        margin-bottom: 8px;
        &::after {
            content: "";
            width: 20px;
            border-radius: 5px;
            height: 3px;
            background: #1c9aa9;
            position: absolute;
            bottom: -2px;
            left: 0;
            right: 0;
            margin-left: auto;
            margin-right: auto;
        }
    }

    & img {
        display: block;
        width: 100%;
    }
`;
const SpotDetails = styled.div`
    width: 45%;
    position: relative;
    & h4 {
        font-size: 24px;
        font-family: "gordita_medium";
        margin-bottom: 20px;
    }
    & h3 {
        font-size: 45px;
        font-family: "EGGIndulekhaUni";
        font-weight: 800;
        max-width: 100%;
        margin-bottom: 30px;
        & span {
            font-size: 45px;
            font-family: "EGGIndulekhaUni";
            color: #0fa76f;
        }
    }
    & p {
        font-size: 18px;
        font-family: "gordita_regular";
        color: #545454;
        width: 90%;
        line-height: 1.3em;
        margin-bottom: 40px;
    }
`;
const Subscribe = styled.div`
    display: flex;
    align-items: center;
    margin-top: 30px;
`;
const ArrowIcon = styled.img`
    display: block;
    position: absolute;
    left: -15%;
    bottom: -12%;
    width: 25%;
    z-index: 3;
`;

const Tefun = styled.div`
    margin-bottom: 20px;
    & img {
        display: block;
    }
`;

const SubscribeBtn = styled(Link)`
    cursor: pointer;
    display: block;
    margin-right: 30px;
    color: #fff;
    font-size: 18px;
    font-family: "gordita_medium";
    width: 238px;
